.modal-content {
    text-align: center!important;
    height: auto;
}

#modal1 {
    z-index: 101;
    width: 91%;
    top: 2%;
}
.modal-close {
    height: 2.13333rem;
    width: 3%;
    line-height: 2.13333rem;
    padding: 0;
    position: absolute;
    z-index: 50;
}

.modal-content > h2 {
    color: #fff;
}
