@media (min-width: 1000px) {
// customize Category product card

.page-sidebar + .page-content .productGrid.categ-grid .product {
    width: 100%;
}

article.card.categ-card > .card-figure {
    margin-top: 3%;
    margin-left: 3%;
    width: 30%;
}



img.card-image.categ-card {
    position: unset;
    transform: none;
}

article.card.categ-card {
    flex-direction: unset;
    height: 350px;
    margin-bottom: 0;
    border: none;
}

.card-body.categ-card > .card-text.card-text--price {
    position: unset;
    width: 40%;
}

.card-body.categ-card {
    display: flex;
    width: 70%;
}

.category-tabs > table {
    width: 100%;
    text-align: center;
}

.category-tabs >table > tbody > tr > th {
    background: #daeef7;
}

li.product.categ-grid {
    margin-bottom: 2%;
}

.card-description {
    width: 60%;
}

a.categ-card-button {
    text-decoration: none;
    background: #48adcb;
    color: #ffffff;
    padding: 10px 30px 10px 30px;
}

.card-text.card-text--price.categ-card > .price-section.price-section--withoutTax {
    margin-bottom: 5%;
}

.product-card-category {
    border: 1px solid #48adcb;
}

.category-tabs > table > tbody > tr > td {
    padding-bottom: 10px;
}

.category-tabs {
    margin: 0 auto;
    width: 95%;
}

.card-description {
    text-align: left;
}

.card-product-description > h3 {
    margin: 0;
    color: #4ec5e7;
}
.view-item-categ-card {
    padding-top: 5%;
}

.card-body.categ-card > .card-description > h4.card-title > a {
    font-weight: 900;
    font-size: 23px;
}

.card-product-description > i.fas.fa-check {
    color: #4ec5e7;
}

.card-image ~ .card-image {
    position: absolute!important;
    top: 0!important;
    left: 0!important;
    opacity: 0;
    transition: opacity 0.5s ease;
}

.card:hover .card-image ~ .card-image, .card:focus .card-image ~ .card-image, .card:active .card-image ~ .card-image {
    opacity: 1!important;
}





















